package loops;
import java.util.Scanner;
public class Pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
Scanner s=new Scanner(System.in);
int n=s.nextInt();
int number=1;
for(int i=1;i<=n;i++)
{
	for(int j=1;j<=n;j++)
	{
	System.out.print(number );
}
	number++;
System.out.println();}
}}
